import React, { Component } from "react";

class AddEducation extends Component {

    constructor(props) {
        super(props);
        this.state = {
        };
        this.handle = this.handle.bind(this)
    }

    handle(e){
        let test = e.target.name;
        let value = e.target.value;
        this.props.change(e.target.id, {[test]: value})
    }

    render(){
        return (            
            <div className="AddEducation" id={this.props.id}>                
                <input placeholder="Título obtenido" onChange={this.handle} name="degree" id={this.props.id}></input>
                <input placeholder="Nombre de la escuela"></input>
                <input placeholder="Información adicional"></input>
                <input placeholder="Fechas"></input>
                <button onClick={this.props.delete}>Eliminar</button>
            </div>
          );
    }  
};

export default AddEducation;
