        this.setState({explist: this.state.explist.filter(i => i.key !== id)});
    }      */

    render(){
        return (
            <div className="makecv">
                <section className="personalinfo">
                    <h2>Información Personal</h2>
                    <input type="text" placeholder="Nombre" id="name" onChange={this.props.change}></input>
                    <input type="text" placeholder="Profesión" id="aboutme" onChange={this.props.change}></input>
                    <label className="inputfile">Subir foto<input type="file" id="aboutme" onChange={this.props.change}></input></label>
                    <input type="email" placeholder="Correo electrónico" id="aboutme" onChange={this.props.change}></input>
                    <input type="tel" placeholder="Teléfono" id="aboutme" onChange={this.props.change}></input>
                    <input type="text" placeholder="Dirección" id="aboutme" onChange={this.props.change}></input>
                    <textarea placeholder="Sobre mí" id="aboutme" onChange={this.props.change}></textarea>
                </section>

                <section className="personalinfo">
                    <h2>Educación</h2>
                    {this.props.state.edulist}                                 
                    <button onClick={async()=>{
                        await this.props.newedu();
                        this.setState({edulist: this.props.state.edulist})                
                        }}>Agregar Educación</button>
                </section>                

                <section className="personalinfo">
                    <h2>Experiencia</h2>
                    {this.props.state.explist}  
                    <button onClick={async()=>{
                        await this.props.newexp();
                        this.setState({explist: this.props.state.explist})                
                        }}>Agregar Experiencia</button>
                </section>

            </div>
        );
    }

};

export default MakeCV;
