import React, { Component } from 'react';
import MakeCV from "./components/makecv";
import ShowCV from "./components/showcv";
import './App.css'
import uniqid from 'uniqid';
import AddEducation from './components/addeducation'
import AddExperience from './components/addexperience'
import ShowEducation from './components/showeducation'
import ShowExperience from './components/showexperience'

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      edulist: [],
      explist: [],
      showedulist: [],
      showexplist: [],
      personalinfo: {
        name: "Luis Espino",
        profession: "Psiconauta",
        picture: "",
        email: "eleseberre@gmail.com",
        phone: "6671015325",
        address: "Jesús Reyes Heroles 2935",
        aboutme: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean elementum leo a est dapibus convallis id vel ex. Quisque sit amet vehicula tortor. Proin tristique vitae lacus sit amet aliquam."
      },
      education: [
        {   id: "1",  degree: "a",      school: "",      addinfo: "",      date: "",    }
      ]
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.newEducation = this.newEducation.bind(this);
    this.newExperience = this.newExperience.bind(this);
    this.deleteExperience = this.deleteExperience.bind(this);
    this.deleteEducation = this.deleteEducation.bind(this);
  }

  async newEducation(){
    const newkey = uniqid();

    let eduobj = {   id: newkey,  degree: "",      school: "",      addinfo: "",      date: "",    }
    await this.setState({education: [...this.state.education, eduobj]})      

    const copy = [<AddEducation key={newkey} delete={this.deleteEducation} change={this.handleChange2} state={this.state} id={newkey}/>];    
    await this.setState({edulist: this.state.edulist.concat(copy)})  

    const copy2 = [<ShowEducation key={newkey} state={this.state} id={newkey}/>]
    await this.setState({showedulist: this.state.showedulist.concat(copy2)})  

    console.log(this.state)
  } 

  async deleteEducation(e){
    const id = e.target.parentElement.id;
    this.setState({edulist: this.state.edulist.filter(i => i.key !== id)});
    this.setState({showedulist: this.state.showedulist.filter(i => i.key !== id)});
    await this.setState({education: this.state.education.filter(i => i.id !== id)});
    console.log(this.state)
  } 

  newExperience(){
    const newkey = uniqid();
    const copy = [<AddExperience key={newkey} delete={this.deleteExperience} id={newkey}/>];
    this.setState({explist: this.state.explist.concat(copy)})      
  } 

  deleteExperience(e){
    const id = e.target.parentElement.id;
    this.setState({explist: this.state.explist.filter(i => i.key !== id)});
  } 

  handleChange = (e) => {
    this.setState({
      personalinfo: {
        ...this.state.personalinfo,
        [e.target.id]: e.target.value,
      },
    });
    console.log(this.state)
  };

  handleChange2 = (e) => {
    let index = this.state.education.findIndex((elem) => elem.id === e.target.id)
    let newArray = [...this.state.education]
    newArray[0] = {...newArray[0], [e.target.name]: e.target.value}

    this.setState({
      education: newArray,
      });
    console.log(this.state)
    this.render()


    this.setState({
      personalinfo: {
        ...this.state.personalinfo,
        [e.target.id]: e.target.value,
      },
    });
    console.log(this.state)
  };

  render(){
    return (
      <div className="App">
        <header className='header'><h1>Crea tu CV</h1></header>
        <main className='main'>
          <MakeCV change={this.handleChange} newedu={this.newEducation} state={this.state} newexp={this.newExperience}/>
          <ShowCV state={this.state}/>
        </main>
        <footer>luiseseberre2023</footer>
      </div>
    );
  }

}

export default App;
