import React, { Component } from "react";
import AddEducation from './addeducation'
import AddExperience from './addexperience'
import uniqid from 'uniqid';

class MakeCV extends Component {

    constructor() {
        super();
        this.state = {
            edulist: [],
            explist: []
        };
        this.newEducation = this.newEducation.bind(this);
        this.deleteEducation = this.deleteEducation.bind(this);
    }

    newEducation(){
        const newkey = uniqid();
        const copy = [<AddEducation key={newkey} delete={this.deleteEducation} id={newkey}/>];
        this.setState({edulist: this.state.edulist.concat(copy)});
    }

    newExperience(){
        const newkey = uniqid();
        const copy = [<AddEducation key={newkey} delete={this.deleteExperience} id={newkey}/>];
        this.setState({explist: this.state.explist.concat(copy)});
    }

    deleteEducation(e){
        const id = e.target.parentElement.id;
        this.setState({edulist: this.state.edulist.filter(i => i.key !== id)});
    }

    deleteExperience(e){
        const id = e.target.parentElement.id;
        this.setState({explist: this.state.explist.filter(i => i.key !== id)});
    }

    render(){
        return (
            <div className="makecv">
                <section className="personalinfo">
                    <h2>Información Personal</h2>
                    <input type="text" placeholder="Nombre"></input>
                    <input type="text" placeholder="Profesión"></input>
                    <label className="inputfile">Subir foto<input type="file"></input></label>
                    <input type="email" placeholder="Correo electrónico"></input>
                    <input type="tel" placeholder="Teléfono"></input>
                    <input type="text" placeholder="Dirección"></input>
                </section>

                <section className="personalinfo">
                    <h2>Educación</h2>
                    {this.state.list}                                 
                    <button onClick={this.example}>Agregar Educación</button>
                </section>


                <section className="personalinfo">
                    <h2>Experiencia</h2>
                    <AddExperience />
                    <button>Agregar Experiencia</button>
                </section>

            </div>
          );
    }

};

export default MakeCV;
